class ToggleLike{constructor(t){this.toggler=t,this.toggleLike()}toggleLike(){$(this.toggler).click((function(t){t.preventDefault();let e=this;$.ajax({type:"POST",url:$(e).attr("href")}).done((function(t){let i=parseInt($(e).attr("data-likes"));console.log(i),1==t.data.deleted?i-=1:i+=1,$(e).attr("data-likes",i),$(e).html(`  <img src="/images/like.png" style="width:30px;height:30px;position: relative;top: 10px;"> ${i} Likes`)})).fail((function(t){console.log("error in completing the request")}))}))}}